import React from "react";
import {
	Map,
	InfoWindow,
	GoogleApiWrapper,
	Polygon,
	Marker
} from "google-maps-react";

class DrawingMap extends React.Component {
	constructor(props) {
		super(props);
		// this.initMap = this.initMap.bind(this);
		// console.log(Polygon);
		this.markerProps = {};

		// this.fillColor = "#32CD32";

		console.log("fill color is", this.fillColor);
		this.initMap.bind(this);
	}
	fillColor = "#32CD32";

	initMap(mapProps, map) {
		console.log("inside initMap", this.fillColor);
		const { google } = mapProps;

		const drawingManager = new google.maps.drawing.DrawingManager({
			drawingMode: null,
			drawingControl: true,
			drawingControlOptions: {
				position: google.maps.ControlPosition.TOP_CENTER,
				drawingModes: [
					// google.maps.drawing.OverlayType.MARKER,
					google.maps.drawing.OverlayType.POLYGON
				]
			},
			markerOptions: {
				icon:
					"https://developers.google.com/maps/documentation/javascript/examples/full/images/beachflag.png"
			},
			polygonOptions: {
				fillColor: this.fillColor
			},
			map: map
		});
		drawingManager.setDrawingMode(google.maps.drawing.OverlayType.POLYGON);
		// drawingManager.polygonOptions.fillColor = this.fillColor;

		//polygon testing

		drawingManager.addListener("polygoncomplete", function(polygon) {
			var coordinates = polygon.getPath().getArray();
			console.info("The coordinates that make up the polygon are");
			//log each set up lat long for the polygon
			coordinates.forEach(function(coord) {
				console.log(coord.lat(), coord.lng());
			});
			//grab and attach value in square feet to the input box with id sqfeet
			document.getElementById("sqfeet").value =
				Math.round(
					google.maps.geometry.spherical.computeArea(polygon.getPath()) *
						10.7639
				) + " Square Feet";
			//log area in square meters
			console.log(
				"Its area in square meters",
				google.maps.geometry.spherical.computeArea(polygon.getPath())
			);
			//create a marker at the point of the first coordinate of each polygon created.
			//  Marker = new google.maps.Marker({
			// 	position: coordinates[0],
			// 	map: Map,
			// 	title:
			// 		Math.round(
			// 			google.maps.geometry.spherical.computeArea(polygon.getPath())
			// 		) *
			// 			10.7639 +
			// 		" Square Feet"
			// });
			//Create a popup window and then add a click listener to open the popup on click or hover
			this.infowindow = new google.maps.InfoWindow({
				content:
					Math.round(
						google.maps.geometry.spherical.computeArea(polygon.getPath())
					) *
						10.7639 +
					" Square Feet"
			});
			// Marker.addListener("click", function() {
			// 	infowindow.open(Map, Marker);
			// });
			// Marker.addListener("mouseover", function() {
			// 	infowindow.open(Map, Marker);
			// 	setTimeout(function() {
			// 		infowindow.close();
			// 	}, 1000);
			// });
		});
		var test = document.getElementById("isLegal");
		test.addEventListener("click", function() {
			console.log("inside init and islegal is clicked.");
		});

		/*events and listeners and blah blah*/
	}

	clickLegalHandler = () => {
		console.log("clicked for legal");
		// this.drawingManager.polygonOptions.fillColor = "#32CD32";
	};
	clickProhibitedHandler = () => {
		console.log("clicked for prohibited");
		// this.drawingManager.polygonOptions.fillColor = "#aaaaaa";
	};

	render() {
		return (
			<div>
				<div>
					<div>
						<h2>Square Feet</h2>
						<input type="text" id="sqfeet" placeholder="value goes here" />
						<br />
						<br />
					</div>
					<h2>Mark Legal or Prohibited</h2>

					<div id="legals">
						<input
							type="radio"
							name="legal"
							value="legal"
							id="isLegal"
							onClick={this.clickLegalHandler}
							defaultChecked
						/>{" "}
						Legal
						<br />
						<input
							type="radio"
							name="legal"
							value="prohibited"
							id="isProhibited"
							onClick={this.clickProhibitedHandler}
						/>{" "}
						Prohibited
						<br />
						<br />
						<br />
					</div>
				</div>
				<br />

				<Map
					google={window.google}
					onReady={this.initMap}
					onClick={this.onMapClicked}
					initialCenter={{ lat: 47.6062, lng: -122.3321 }}
					zoom={15}
					eMapApiInternals>
					<Marker
						title={"The marker`s title will appear as a tooltip."}
						name={"Downtown"}
						position={{ lat: 47.60629, lng: -122.3321 }}
					/>
				</Map>
			</div>
		);
	}
}

export default GoogleApiWrapper({
	apiKey: "AIzaSyAKWE1JinLb5yLSoxHiEjiq1CMuOqbx_s4",
	libraries: ["drawing"]
})(DrawingMap);

